package Basics1;

public class ArithematicOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//operators arithmetic
		
		int a = 20;
		int b =30;
		
		System.out.println(a+b); // syso control plus spacebar
		
		//system -->class
		//out-->reserve keyword to take output
		//println -->method
		
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b); //result quotient
		System.out.println(a%b); //result remainder
		

	}

}
