package Basics1;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//logical operators-->AND,OR,NOT
		
		boolean a = true;
		boolean b =false;
		
		System.out.println(a||b);
		
		System.out.println(a&&b);
		
	    System.out.println(!b);

	}

}
