package Basics2;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//wap to print numbers between 1 to 10 using do while
		
		int i=1; //initialization
		
		do
		{
			System.out.println(i);
			i++; //updation
		}
		while(i<=10);//condition
		

	}

}
