package String;

public class Diffeqallsandequal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//diff b/w equal() and ==
		
		String str1 = new String("java");
		String str2 = new String("java");
		
		System.out.println(str1.equals(str2));//compares string data
		System.out.println(str1==str2);//compares object reference
		

	}

}
